import { createSlice } from "@reduxjs/toolkit";
//services
import { adminHighestRatedBranch } from "../services/rank_service";
import { adminLowestRatedBranch } from "../services/admin_lowest_rated_branch_service";
import { getRestaurantChainReview } from "../services/admin_restaurant_chain_review_service";
import {
  getAllRestaurantMonthlyEarnings,
  getAnnualEarningsAnalysis,
  getMarketMostSellingCities,
  getRestaurantMonthlyEarnings,
  getRestaurantMostSellingCities,
} from "../services/restaurant_service";

// ----------------------------------------------------------------------

const slice = createSlice({
  name: "resturants",
  initialState: {
    isLoading: false,
    error: null,
    rateBranch: {},
    lowestRateBransh: {},
    chainReview: [],
    allRestaurantMonthlyEarnings: {
      current: [],
      previous: [],
      currency: "",
    },
    
    branchesMostSellingCities:[],
    isLoadingMostSellingCities:false,
    annualEarningsAnalysis: {
      current: [],
      previous: [],
      currency: "",
    },
  },
  reducers: {},
  extraReducers: {
    // get admin lowest rated branch
    [adminLowestRatedBranch.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.lowestRateBransh = action.payload.data;
    },
    [adminLowestRatedBranch.rejected]: (state, action) => {
      state.isLoading = false;
    },
    [adminLowestRatedBranch.pending]: (state) => {
      state.isLoading = true;
    },

    // Get Resturant Chain Review
    [getRestaurantChainReview.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.chainReview = action.payload.data;
    },
    [getRestaurantChainReview.rejected]: (state, action) => {
      state.isLoading = false;
    },
    [getRestaurantChainReview.pending]: (state) => {
      state.isLoading = true;
    },

    // // Get Restaurant Monthly Earning
    // [getAllRestaurantMonthlyEarnings.fulfilled]: (state, action) => {
    //   // console.log('fullfilled')
    //   state.isLoading = false;
    //   // console.log(action.payload.data)
    //   state.allRestaurantMonthlyEarnings = action.payload.data;
    // },
    // [getAllRestaurantMonthlyEarnings.rejected]: (state, action) => {
    //   // console.log('rejected')
    //   state.isLoading = false;
    // },
    // [getAllRestaurantMonthlyEarnings.pending]: (state) => {
    //   // console.log('pending')
    //   state.isLoading = true;
    // },


    // Get Annual Earning Analysis
    // [getAnnualEarningsAnalysis.fulfilled]: (state, action) => {
    //   console.log("fullfilled");
    //   state.isLoading = false;
    //   console.log(action.payload.data);
    //   state.annualEarningsAnalysis = action.payload.data;
    // },
    // [getAnnualEarningsAnalysis.rejected]: (state, action) => {
    //   // console.log('rejected')
    //   state.isLoading = false;
    // },
    // [getAnnualEarningsAnalysis.pending]: (state) => {
    //   // console.log('pending')
    //   state.isLoading = true;
    // },

    // Get Resturant Most Selling Cities  Review
    [getRestaurantMostSellingCities.fulfilled]: (state, action) => {
      state.isLoadingMostSellingCities = false;
      state.branchesMostSellingCities = action.payload.data;
    },
    [getRestaurantMostSellingCities.rejected]: (state, action) => {
      state.isLoadingMostSellingCities = false;
    },
    [getRestaurantMostSellingCities.pending]: (state) => {
      state.isLoading = true;
    },

    // Get Resturant Most Selling Cities  Review
    [getMarketMostSellingCities.fulfilled]: (state, action) => {
      state.isLoadingMostSellingCities = false;
      state.branchesMostSellingCities = action.payload.data;
    },
    [getMarketMostSellingCities.rejected]: (state, action) => {
      state.isLoadingMostSellingCities = false;
    },
    [getMarketMostSellingCities.pending]: (state) => {
      state.isLoading = true;
    },

  },
});

// Reducer
export default slice.reducer;
