import { createSlice } from "@reduxjs/toolkit";
import {
  getAdminProfileInfo,
  editAdminProfileInfo,
  updateAdminEmail,
  updateAdminPassword,
  updateAdminPhoto,
  getOverView,
  editMyProfileInfo,
  updateProfilePhoneNumber,
} from "../services/admin_profile_service";

//----------------------

const slice = createSlice({
  name: "adminProfile",
  initialState: {
    isLoading: true,
    error: null,
    editIsSuccessful: false,
    updateEmailIsSuccess: false,
    updatePasswordIsSuccess: false,
    personalInfo: {},
    adminInfo: {},
    isLoadingUpdateEmail: false,
    isLoadingUpdatePassword: false,
    isLoadingUpdateMyProfile: false,
    updateMyProfileIsSuccess: false,
    isLoadingUpdatePhonNumber: false,
  },
  reducers: {
    editMode: (state, action) => {
      state.editIsSuccessful = false;
    },
    setInitial: (state, action) => {
      // state.editIsSuccessful = false;
      state.updateEmailIsSuccess = false;
      state.updatePasswordIsSuccess = false;
      state.updateMyProfileIsSuccess = false;
      state.updatePhonNumberIsSuccess = false;
    },
  },
  extraReducers: {
    // ==> GetAdminProfileInfo
    [getAdminProfileInfo.fulfilled]: (state, action) => {
      console.log("fulfilled");
      state.isLoading = false;
      state.personalInfo = action.payload.data;
    },
    [getAdminProfileInfo.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = false;
    },
    [getAdminProfileInfo.pending]: (state) => {
      state.isLoading = true;
    },

    //==> EditAdminProfileInfo
    [editAdminProfileInfo.fulfilled]: (state, action) => {
      console.log("fulfilled");
      if (action.payload.message === "Success") {
        state.editIsSuccessful = true;
      }
    },
    [editAdminProfileInfo.rejected]: (state, action) => {
      console.log("rejected");
      state.error = false;
    },
    [editAdminProfileInfo.pending]: (state, action) => {
      console.log("pending");
    },

    // ==> UpdateAdminEmail
    [updateAdminEmail.fulfilled]: (state, action) => {
      console.log("fulfilled update email");
      state.isLoadingUpdateEmail = false;
      if (action.payload.message === "Success") {
        state.updateEmailIsSuccess = true;
        alert("Email Is Updated");
      }
    },
    [updateAdminEmail.rejected]: (state, action) => {
      state.isLoadingUpdateEmail = false;
      state.error = false;
    },
    [updateAdminEmail.pending]: (state, action) => {
      state.isLoadingUpdateEmail = true;
    },

    // ==> UpdateAdminPassword
    [updateAdminPassword.fulfilled]: (state, action) => {
      state.isLoadingUpdatePassword = false;

      console.log("fulfilled");
      if (action.payload.message === "Success") {
        state.updatePasswordIsSuccess = true;
        alert("Password Is Updated");
      }
    },
    [updateAdminPassword.rejected]: (state, action) => {
      state.isLoadingUpdatePassword = false;
      console.log("edit fa4l");
    },
    [updateAdminPassword.pending]: (state, action) => {
      state.isLoadingUpdatePassword = true;
    },

    // ==> UpdateAdminPassword
    [updateAdminPhoto.fulfilled]: (state, action) => {
      console.log("zy el fool");
    },
    [updateAdminPhoto.rejected]: (state, action) => {
      console.log("edit fa4l");
    },
    [updateAdminPhoto.pending]: (state, action) => {
      console.log("edit lesa 4wya");
    },

    // Get OverView Admin
    [getOverView.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.adminInfo = action.payload.data;
    },
    [getOverView.rejected]: (state, action) => {
      state.isLoading = false;
      state.error = false;
    },
    [getOverView.pending]: (state) => {
      state.isLoading = true;
    },

    // ==> Updat My Profile Info
    [editMyProfileInfo.fulfilled]: (state, action) => {
      console.log("fulfilled update profile");
      state.isLoadingUpdateMyProfile = false;
      if (action.payload.message === "Success") {
        state.updateMyProfileIsSuccess = true;
        alert("profile Is Updated");
      }
