import React from "react";
// helmet
import { Helmet } from "react-helmet-async";
// react-router-dom
import { Link as RouterLink, useNavigate } from "react-router-dom";
// Locales
import { useLocales } from "../../../locales";
//@Mui
import {
  Alert,
  Box,
  Button,
  Container,
  Typography,
  Grid,
  IconButton,
  InputAdornment,
  MenuItem,
  Stack,
} from "@mui/material";
import Link from "@mui/material/Link";
// Form
import * as Yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
// Components
import { useSettingsContext } from "../../../components/settings";
import FormProvider, {
  RHFTextField,
  RHFSelect,
} from "../../../components/hook-form";
// Section Components
import BreadCrumbs from "../../../components/breadCrumbs/BreadCrumbs";
import { useTheme } from "@emotion/react";
import { useDispatch, useSelector } from "../../../redux/store";
import { useEffect } from "react";
import {
  addMarketProduct,
  getMarketCategories,
  getSubCategoriesByMarketId,
} from "../../../redux/services/branch_market_service";
import { useState } from "react";
import { LoadingButton } from "@mui/lab";
// --------------------------------------------------------------------------------------

// ------------ Style --------------

const mainHolderSx = {
  p: {
    xs: "30px 30px 48px 20px",
    sm: "30px 42px 48px 42px",
  },
  borderRadius: "16px",
  background: "#FFFFFF",
  boxShadow: "2px 2px 10px rgba(0, 0, 0, 0.1)",
  position: "relative",
};

const txtFieldBorderStyle = {
  "& .MuiOutlinedInput-input": {
    fontSize: "16px",
    fontWeight: 400,
    lineHeight: "16px",
    color: "#121212",
  },
  "& .MuiInputLabel-root": {
    color: "#9DA0A4",
    fontWeight: 400,
    fontSize: "14px",
  },
  "& .MuiOutlinedInput-root": {
    pr: "20px",
    height: "48px",
    // alignItems: 'baseline',
    "& fieldset": {
      border: "1.5px solid #bcbcbc",
    },
    "&:hover fieldset": {
      borderColor: "rgba(255, 204, 99, 0.3)",
    },
    "&.Mui-focused fieldset": {
      borderColor: "rgba(255, 204, 99, 0.3)",
    },
  },
};

const transparentBorderTxtField = {
  "& .MuiOutlinedInput-input": {
    fontSize: "16px",
    fontWeight: 400,
    lineHeight: "22px",
    color: "#535353",
    paddingRight: "30px",
    pr: "5px!important",
    pl: "3px",
  },
  "& .MuiFormLabel-root": {
    color: "#BCBCBC",
    fontSize: "16px",
    top: "-3px",
  },
  "& .MuiOutlinedInput-root": {
    height: "48px",
    pr: "5px",
    "& fieldset": {
      borderColor: "transparent!important",
    },
    "&:hover fieldset": {
      borderColor: "transparent!important",
    },
    "&.Mui-focused fieldset": {
      borderColor: "transparent!important",
    },
  },
};

const containedButtonStyle = {
  fontWeight: "600",
  fontSize: "14px",
  lineHeight: "22px",
  color: "#FFFFFF",
  background: "#FFCC63",
  paddingLeft: { lg: "70px", sm: "70px", xs: "42px" },
  paddingRight: { lg: "70px", sm: "70px", xs: "42px" },
  paddingTop: "12px",
  paddingBottom: "12px",
  borderRadius: "8px",
  width: "100%",
};

const outLinedButtonStyle = {
  fontWeight: "600",
  fontSize: "14px",
  lineHeight: "22px",
  color: " #FFCC63",
  border: "0.901691px solid #FFCC63",
  background: "#FFFFFF",
  paddingLeft: { lg: "85px", sm: "85px", xs: "52px" },
  paddingRight: { lg: "85px", sm: "85px", xs: "52px" },
  paddingTop: "12px",
  paddingBottom: "12px",
  borderRadius: "8px",
  marginRight: "18px",
  width: "100%",
};

export default function AddMarketProductPage() {
  const navigate = useNavigate();

  const { translate } = useLocales();

  const { themeStretch } = useSettingsContext();

  const theme = useTheme();

  const [categoryId, setCategoryId] = useState(undefined);

  const [marketSubCategoryId, setMarketSubCategoryId] = useState();

  const [file, setFile] = useState(null);

  const { branchId } = useSelector((state) => state.control);

  const dispatch = useDispatch();

  const { marketCategory, subCategory, isLoadingAddMarketProduct } =
    useSelector((state) => state.branchMarket);

  const arabicRGX = /^[\u0621-\u064A]+$/;

  const ProductSchema = Yup.object().shape({
    Name: Yup.string().required(translate("product_name_en_is_required")),
    SecondName: Yup.string()
      .required(translate("arabic_product_name_is_required"))
      .matches(arabicRGX, translate("only_arbic_letters")),
    ImageUrl:
      file !== null
        ? ""
        : Yup.string().required(translate("product_photo_is_required")),

    Description: Yup.string()
      .required(translate("description_en_is_required"))
      .min(20),
    SecondDescription: Yup.string()
      .required(translate("description_ar_is_required"))
      .min(20),
    categoryId:
      categoryId !== undefined
        ? ""
        : Yup.string().required(translate("category_is_required")),
    MarketSubCategoryId: Yup.string().required(
      translate("sub_category_name_is_required")
    ),
    Unit: Yup.string().required(translate("unit_is_required")),
    Price: Yup.string().required(translate("minimum_order_is_required")),
  });

  const defaultValues = {
    Name: "",
    SecondName: "",
    ImageUrl: "",
    Description: "",
    SecondDescription: "",
    categoryId: categoryId,
    SubCategory: marketSubCategoryId,
    Unit: "",
    Price: "",
  };

  const methods = useForm({
    resolver: yupResolver(ProductSchema),
    defaultValues,
  });

  const {
    reset,
    setError,
    setValue,
    watch,
    handleSubmit,
    formState: { errors, isSubmitting, isSubmitSuccessful, isDirty },
  } = methods;

  const watchImageUrl = watch("ImageUrl");

  let formData = new FormData();

  const addImageUrl = async (e) => {
    setFile(e.target.files[0]);
    formData.append("ImageUrl", e.target.files[0]);
  };

  const onSubmit = async (data) => {
    try {
      formData.append("Name", data.Name);
      formData.append("SecondName", data.SecondName);
      formData.append("Description", data.Description);
      formData.append("SecondDescription", data.SecondDescription);
      formData.append("MarketSubCategoryId", data.MarketSubCategoryId);
      formData.append("ImageUrl", file);
      formData.append("Unit", data.Unit);
      formData.append("Price", data.Price);
      dispatch(addMarketProduct(formData));
    } catch (error) {
      reset();
    }
  };
  useEffect(() => {
    dispatch(getMarketCategories(branchId));
  }, [categoryId]);
  const breadcrumbs = [
    <Link
      sx={{
        fontWeight: 400,
        fontSize: "12px",
        color: "#9DA0A4",
      }}
      underline="hover"
      key="1"
      component={RouterLink}
      to="/employee/market-menu"
    >
      {translate(`market_menu`)}
    </Link>,

    <Typography
      sx={{
        fontWeight: "700 !important",
        fontSize: "12px !important",
        color: "#121212",
      }}
      key="2"
    >
      {translate(`add_new_product`)}
    </Typography>,
  ];

  // ------------- JSX Code --------------
  return (
    <>
      <Helmet>
        <title> Add New Market Product </title>
      </Helmet>

      <Container maxWidth={themeStretch ? false : "xl"}>
        <BreadCrumbs breadcrumbs={breadcrumbs} />

        <Box sx={{ ...mainHolderSx }}>
          <Box display="flex" mb="24px" alignItems="center">
            <IconButton
              disableRipple
              onClick={() => {
                navigate("/employee/market-menu");
              }}
              sx={{
                width: "25px",
                transform: theme.direction === "rtl" && "rotate(180deg)",
              }}
            >
              <img src="/assets/icons/dashboard/ic_backArrow.svg" alt="" />
            </IconButton>
            {/* ----- Title -----  */}
            <Typography variant="h5" color="#ffcc63" ml="13px">
              {translate(`add_new_product`)}
            </Typography>
          </Box>

          <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
            {!!errors.afterSubmit && (
              <Alert severity="error">{errors.afterSubmit.message}</Alert>
            )}

            {/* ************ Start Add New Product ************/}
            <Grid container rowSpacing={2} columnSpacing={8}>
              {/* ------------ Left Side Inputs ------- */}
              <Grid item xs={12} md={6}>
                <Grid container rowSpacing={2} columnSpacing="16px">
                  {/* product name en  input */}
                  <Grid item xs={12} md={6}>
                    <RHFTextField
                      name="Name"
                      label={translate("product_name_en")}
                      type="text"
                      sx={{ ...txtFieldBorderStyle }}
                    />
                  </Grid>

                  {/* product name ar  input */}
                  <Grid item xs={12} md={6}>
                    <RHFTextField
                      name="SecondName"
                      label={translate("product_name_ar")}
                      type="text"
                      sx={{ ...txtFieldBorderStyle }}
                    />
                  </Grid>

                  {/* product photo input */}
                  <Grid item xs={12}>
                    <RHFTextField
                      name="ImageUrl"
                      label={translate("product_photo")}
                      value={file?.name}
                      sx={{
                        ...txtFieldBorderStyle,
                        "& .MuiOutlinedInput-input": {
                          color: file ? "#535353" : "transparent",
                          fontSize: "14px",
                        },
                      }}
                      InputProps={{
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              color="primary"
                              aria-label="upload picture"
                              component="label"
                            >
                              <label htmlFor="ImageUrl">
                                <img
                                  src="/assets/icons/auth/ic_upload.svg"
                                  alt="upload_icon"
                                />
                                <input
                                  hidden
                                  accept="image/*"
                                  type="file"
                                  onChange={addImageUrl}
                                />
                              </label>
                            </IconButton>
                          </InputAdornment>
                        ),
                      }}
                    />
                  </Grid>

                  {/*Category Select*/}

                  <Grid item xs={12}>
                    <RHFSelect
                      name="categoryId"
                      type="text"
                      label={translate("categories")}
                      sx={{ ...txtFieldBorderStyle }}
                      // value={categoryId}
                      onChange={(e) => {
                        setCategoryId(e.target.value);
                        dispatch(
                          getSubCategoriesByMarketId({
                            marketId: branchId,
                            categoryId: e.target.value,
                          })
                        );
                      }}
                    >
                      {marketCategory.map((option, index) => (
                        <>
                          <option hidden />
                          <option key={index} value={option.id}>
                            {option.name}
                          </option>
                        </>
                      ))}
                    </RHFSelect>
                  </Grid>

                  {/*Sub-Category Select*/}
                  <Grid item xs={12}>
                    <RHFSelect
                      name="MarketSubCategoryId"
                      type="text"
                      label={translate("sub_category")}
                      sx={{ ...txtFieldBorderStyle }}
                      /*  value={marketSubCategoryId}
                      onChange={(e) => {
                        setMarketSubCategoryId(e.target.value);
                      }}*/
                    >
                      {subCategory.map((option, index) => (
                        <>
                          <option hidden />
                          <option key={index} value={option.id}>
                            {option.name}
                          </option>
                        </>
                      ))}
                    </RHFSelect>
                  </Grid>

                  {/* product  size  input 
                  <Grid item xs={12} md={6}>
                    <RHFTextField
                      name="Size"
                      type="text"
                      label={translate("size")}
                      sx={{ ...txtFieldBorderStyle }}
                    />
                  </Grid>*/}

                  {/* product unit  input */}
                  <Grid item xs={12} md={12}>
                    <RHFTextField
                      name="Unit"
                      type="text"
                      label={translate("unit_kg_litter")}
                      sx={{ ...txtFieldBorderStyle }}
                    />
                  </Grid>

                  {/* product minimum order input*/}
                  <Grid item xs={12}>
                    <RHFTextField
                      name="Price"
                      type="number"
                      sx={{ ...txtFieldBorderStyle }}
                      label={translate(`minmum_order`)}
                      // placeholder={translate(`minmum_order`)}
                      InputProps={{
                        startAdornment: (
                          <RHFSelect
                            name="Currency"
                            sx={{
                              ...transparentBorderTxtField,
                              width: "80px",
                            }}
                            onChange={(e) =>
                              setValue("Currency", e.target.value, {
                                shouldValidate: true,
                              })
                            }
                          >
                            <MenuItem value="AED">AED</MenuItem>
                            <MenuItem value="EGP">EGP</MenuItem>
                          </RHFSelect>
                        ),
                      }}
                    />
                  </Grid>

                  {/* product discount  input 
                  <Grid item xs={12}>
                    <RHFTextField
                      name="Discount"
                      type="text"
                      label={translate("discount")}
                      sx={{ ...txtFieldBorderStyle }}
                    />
                  </Grid>*/}
                </Grid>
              </Grid>

              {/* ------------ Right Side Inputs ------- */}
              <Grid item xs={12} md={6}>
                <Grid container rowSpacing={2}>
                  {/* Description En Inputs */}
                  <Grid item xs={12}>
                    <RHFTextField
                      multiline
                      rows={3.9}
                      name="Description"
                      label={translate("description_English")}
                      type="text"
                      sx={{
                        ...txtFieldBorderStyle,
                        "& .MuiOutlinedInput-root": {
                          height: "114px",
                          "& fieldset": {
                            borderColor: "#bcbcbc",
                          },
                          "&:hover fieldset": {
                            borderColor: "rgba(255, 204, 99, 0.3)",
                          },
                          "&.Mui-focused fieldset": {
                            borderColor: "rgba(255, 204, 99, 0.3)",
                          },
                        },
                      }}
                    />
                  </Grid>
                  {/* Description Ar Inputs */}
                  <Grid item xs={12}>
                    <RHFTextField
                      multiline
                      rows={3.9}
                      name="SecondDescription"
                      label={translate("description_arabic")}
                      type="text"
                      sx={{
                        ...txtFieldBorderStyle,
                        "& .MuiOutlinedInput-root": {
                          height: "114px",
                          "& fieldset": {
                            borderColor: "#bcbcbc",
                          },
                          "&:hover fieldset": {
                            borderColor: "rgba(255, 204, 99, 0.3)",
                          },
                          "&.Mui-focused fieldset": {
                            borderColor: "rgba(255, 204, 99, 0.3)",
                          },
                        },
                      }}
                    />
                  </Grid>
                </Grid>
              </Grid>
            </Grid>

            {/* =========@@ Action Button @@========*/}
            <Grid container columnSpacing={8}>
              <Grid item xs={12} md={6} />

              <Grid item xs={12} md={6}>
                <Stack direction="row" mt="48px">
                  <Button
                    variant="outlined"
                    sx={outLinedButtonStyle}
                    onClick={() => navigate("/employee/market-menu")}
                  >
                    {translate("back")}
                  </Button>

                  <LoadingButton
                    sx={{ ...containedButtonStyle }}
                    type="submit"
                    variant="Contained"
                    loading={isLoadingAddMarketProduct}
                  >
                    {translate(`preview`)}
                  </LoadingButton>
                </Stack>
              </Grid>
            </Grid>
          </FormProvider>
        </Box>
      </Container>
    </>
  );
}
