import React from "react";
import { useTheme } from "@emotion/react";
//Locales
import { useLocales } from "../../../locales";
// mui
import { Box, Stack, Typography } from "@mui/material";
// components
import Iconify from "../../iconify/Iconify";
// Url
import * as URLS from "../../../urls";
// react-router-dom
import { useNavigate } from "react-router-dom";
import { useDispatch } from "../../../redux/store";
import { setBranchId } from "../../../redux/slices/control";

// -------------------------------------------------------------------

// ----------- Styles --------------
const mainHolderSx = {
  borderRadius: "25px",
  boxShadow: "0px 0px 8px rgba(1, 1, 1, 0.12)",
  background: "#FFFFFF",
  p: "24px",
  position: "relative",
  height: "136px",
};

const avatarStyle = {
  width: "56px",
  height: "56px",
  borderRadius: "50%",
  border: "1px solid #ffcc63",
};

const titleTextSx = {
  color: "#A6AAB4",
  fontSize: "14px",
  fontWeight: "500",
};

const rateTextSx = {
  color: "#121212",
  opacity: 0.74,
  fontSize: "12px",
  fontWeight: "700",
};

const highLightTextSx = {
  color: "#FFCC63",
  fontSize: "13px",
  fontWeight: "700",
  lineHeight: "12px",
};

const smallTextSx = {
  color: "#ABABAB",
  fontSize: "12px",
  fontWeight: "400",
  textDecoration: "underline",
};

const arrowIconsStyle = {
  width: "8px",
  height: "9px",
};

const miniTextSx = {
  color: '#4EE476',
  fontWeight: 600,
  fontSize: '12px'
}

export default function RatedBranchCard({ title, data }) {
  const { translate } = useLocales();

  const dispatch = useDispatch();

  const theme = useTheme();

  const navigate = useNavigate();

  // -------------- JSX Code -----------
  return (
    <>
      <Box sx={{ ...mainHolderSx }}>
        {/*  --------- Start Title and more Btn  ---------- */}
        <Stack
          direction="row"
          alignItems="center"
          justifyContent="space-between"
          mb="9px"
        >
          <Typography sx={{ ...titleTextSx }}>
            {translate(`${title}`)}
          </Typography>
        </Stack>

        {/*  -------------- Content Holder  ------------------ */}
        <Stack direction="row" alignItems="center">
          {/* ---- avater holder --- */}
          <Box pr="21px">
            <img
              src={
                localStorage.getItem("facilityType") === "0"
                  ? URLS.baseImageURL + `resturant/${data?.imgUrl}`
                  : URLS.baseImageURL + `market/${data?.imgUrl}`
              }
              alt=""
              style={avatarStyle}
            />
          </Box>

          {/* ---- branch name and rate holder --- */}
          <Stack>
            <Typography sx={{ ...highLightTextSx, mb: "6px" }}>
              {data?.name}
            </Typography>
            {title === "the_highest_rated_branch" ? (
              <Stack direction="row" alignItems="center" mb="8px">
                <img
                  src="/assets/icons/dashboard/ic_star.svg"
                  alt="Avatar"
                  style={{ width: "12px", height: "12px" }}
                />
                <Typography sx={{ ...rateTextSx, ml: "5px" }}>
                  {data?.rate}
                </Typography>
              </Stack>
            ) : (
                <Typography sx={{ ...miniTextSx}}>
                  {data?.currency} {data?.balance}
                </Typography>
            )}

            <Box display="flex" alignItems="center">
              <Typography
                onClick={() => {
                  dispatch(setBranchId(data?.id));
                  navigate("/employee/branch-info");
                }}
                sx={{
                  ...smallTextSx,
                  cursor: "pointer",
                  mr: "8px",
                }}
              >
                {translate("view_branch_details")}
              </Typography>

              <img
                src="/assets/icons/arrow/arrow.svg"
                alt="Avatar"
                style={{
                  ...arrowIconsStyle,
                  transform: theme.direction === "rtl" && "rotate(180deg)",
                }}
              />
            </Box>
          </Stack>
        </Stack>
      </Box>
    </>
  );
}
