import React, { useState, useEffect } from "react";
// Locales
import { useLocales } from "../../../../locales";
// @MUi
import {
  Button,
  IconButton,
  Table,
  Tooltip,
  TableBody,
  TableContainer,
  CircularProgress,
  Box,
  Stack,
  Typography,
} from "@mui/material";
import { makeStyles } from "@material-ui/core/styles";
import { styled } from "@mui/material/styles";
// Mock
import { _MenuList } from "../../../../_mock/arrays";
// Components
import {
  useTable,
  getComparator,
  emptyRows,
  TableNoData,
  TableEmptyRows,
  TableHeadCustom,
  TableSelectedAction,
} from "../../../../components/table";
import MarketSubCategoryPagination from "../../../../components/pagination/MarketSubCategoryPagination";
import CustomConfirmDialog from "../../../../components/confirm-dialog/CustomConfirmDialog";
import Iconify from "../../../../components/iconify";
import Scrollbar from "../../../../components/scrollbar";
// Sections Components
import Header from "../details/Header";
import MarketSubCategoryTableRow from "./MarketSubCategoryTableRow";
import { getMarketCategoriesDetails } from "../../../../redux/services/markets_service";
import { useDispatch, useSelector } from "react-redux";
import { Initialize } from "../../../../redux/slices/market";
import { DownloadTableExcel } from 'react-export-table-to-excel';
import Image from "../../../../components/image/Image";
import { useRef } from "react";
import { useReactToPrint } from 'react-to-print';
// -------------------------------------------------------------------------------------------

// ------------ Style ------------------
const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiPaginationItem-icon": {
      color: "#FFCC63;",
    },
  },
}));

const StyledTableContainer = styled(TableContainer)(({ theme }) => ({
  position: "relative",
  overflow: "unset",
  boxShadow: " 2px 2px 10px rgba(0, 0, 0, 0.1)",
  borderRadius: "16px",
  background: "#FFFFFF",
}));

const tableHolderSx = {
  minWidth: 800,
  borderCollapse: "separate",
  borderSpacing: "0px 16px",
  paddingRight: "19px",
  paddingLeft: "19px",
};

const cancelBtnSx = {
  background: "#FFFFFF",
  color: "#535353",
  border: "0.75px solid #BCBCBC",
  fontSize: "12px",
  fontWeight: "500",
  "&:hover": {
    bgcolor: "text.primary",
    color: (theme) =>
      theme.palette.mode === "light" ? "common.white" : "grey.800",
  },
  boxShadow: " 0px 0.75px 1.5px rgba(16, 24, 40, 0.05)",
  borderRadius: "6px",
  paddingLeft: "43px",
  paddingRight: "43px",
};

const confirmBtnSx = {
  fontSize: "12px",
  fontWeight: "500",
  "&:hover": {
    bgcolor: "text.primary",
    color: (theme) =>
      theme.palette.mode === "light" ? "common.white" : "grey.800",
  },
  boxShadow: " 0px 0.75px 1.5px rgba(16, 24, 40, 0.05)",
  borderRadius: "6px",
  paddingLeft: "44px",
  paddingRight: "44px",
};

const totalUserSx = {
  fontSize: "16px",
  fontWeight: 600,
  color: "#9DA0A4",
};
const infoSx = {
  fontSize: "10px",
  fontWeight: 700,
  color: "#BCBCBC",
};

const actionHolderSx = {
  flexDirection: 'row',
  justifyContent: "space-between",
  alignItems: "center",
  spacing: 2,
  mb: "24px",
};

const TABLE_HEAD = [
  { id: "category_name", label: "category_name", align: "center" },
  { id: "main_category_name", label: "main_category_name", align: "center" },
  { id: "number_of_items", label: "number_of_items", align: "center" },
  { id: "number_of_orders", label: "number_of_orders", align: "center" },
  { id: "category_status", label: "category_status", align: "center" },
  { id: "action", label: "action", align: "left" },
];

export default function MarketSubCategoryList() {
  const dispatch = useDispatch();

  const { marketCategories, isLoadingmarketCategories, isDeletedCategories } =
    useSelector((state) => state.market);

  const { branchId } = useSelector((state) => state.control);
  var marketId = branchId;

  const classes = useStyles();
  const {
    dense,
    page,
    order,
    orderBy,
    rowsPerPage,
    setPage,
    selected,
    setSelected,
    onSelectRow,
    onSelectAllRows,
    onSort,
    onChangeDense,
    onChangePage,
    onChangeRowsPerPage,
  } = useTable();
  const { translate } = useLocales();
  const [tableData, setTableData] = useState(marketCategories);
  const [openConfirm, setOpenConfirm] = useState(false);
  const [filterName, setFilterName] = useState("");
  const [filterRole, setFilterRole] = useState("all");
  const [filterStatus, setFilterStatus] = useState("all");

  const dataFiltered = applyFilter({
    inputData: tableData,
    comparator: getComparator(order, orderBy),
    filterName,
    filterRole,
    filterStatus,
  });
  const dataInPage = dataFiltered?.slice(
    page * rowsPerPage,
    page * rowsPerPage + rowsPerPage
  );
  const denseHeight = dense ? 52 : 72;
  const isFiltered =
    filterName !== "" || filterRole !== "all" || filterStatus !== "all";
  const isNotFound =
    (!dataFiltered?.length && !!filterName) ||
    (!dataFiltered?.length && !!filterRole) ||
    (!dataFiltered?.length && !!filterStatus);

  const handleOpenConfirm = () => {
    setOpenConfirm(true);
  };

  const handleCloseConfirm = () => {
    setOpenConfirm(false);
  };

  const handleDeleteRow = (id) => {
    const deleteRow = tableData?.filter((row) => row.id !== id);
    setSelected([]);
    setTableData(deleteRow);

    if (page > 0) {
      if (dataInPage.length < 2) {
        setPage(page - 1);
      }
    }
  };

  const handleDeleteRows = (selected) => {
    const deleteRows = tableData?.filter((row) => !selected.includes(row.id));
    setSelected([]);
    setTableData(deleteRows);

    if (page > 0) {
      if (selected.length === dataInPage.length) {
        setPage(page - 1);
      } else if (selected.length === dataFiltered?.length) {
        setPage(0);
      } else if (selected.length > dataInPage.length) {
        const newPage =
          Math.ceil((tableData?.length - selected.length) / rowsPerPage) - 1;
        setPage(newPage);
      }
    }
  };

  const SubCategoriesRef = useRef(null);


  const handlePrint = useReactToPrint({
    content: () => SubCategoriesRef.current,
  });

  useEffect(() => {
    setTableData(marketCategories);
    //dispatch(getMarketCategoriesDetails({ pageNumber: 1, marketId }));

    // fetch data again after delete item
    if (isDeletedCategories) {
      dispatch(
        getMarketCategoriesDetails({
          pageNumber: 1,
          marketId,
        })
      );
    }
    dispatch(Initialize());
  }, [dispatch, isDeletedCategories, marketCategories]);

  const Spinner = (
    <Box display="flex" justifyContent="center">
      <CircularProgress />{" "}
    </Box>
  );
  // ------------- JSX Code ----------------
  return (
    <>
      {/* --------- Start Table Header ---------*/}
      {/* <Header counter={marketCategories.length} counterTitle="sub_categories" /> */}

      <Stack sx={{ ...actionHolderSx }}>
        {/* ***** total user **** */}

        <Typography sx={{ ...totalUserSx }}>
          {translate("total_sub_categories")}: {marketCategories?.length}
        </Typography>

        {/* ---- print and export ---- */}
        <Stack
          direction="row"
          alignItems="center"
          spacing="18px"
        >
          {/* ----- Print ----- */}
          <Button onClick={handlePrint}>
            <Image
              disabledEffect
              src="/assets/icons/dashboard/ic_print.svg"
            />

            <Typography sx={{ ...infoSx, ml: '8px' }}>
              {translate("print")}
            </Typography>
          </Button>

          {/* ----- Export ----- */}
          <Stack direction="row" alignItems="center" spacing={1}>

            <DownloadTableExcel
              filename="SubCategoriesTable"
              sheet="SubCategories"
              currentTableRef={SubCategoriesRef.current}
            >

              <Button disableRipple>
                <Image
                  disabledEffect
                  src="/assets/icons/dashboard/ic_export.svg"
                />

                <Typography sx={{ ...infoSx, ml: '8px' }}>
                  {translate("export")}
                </Typography>
              </Button>

            </DownloadTableExcel>

          </Stack>
        </Stack>
      </Stack>

      {/* -------------- Start Table ---------------*/}
      <StyledTableContainer>
        <TableSelectedAction
          dense={dense}
          numSelected={selected.length}
          rowCount={tableData?.length}
          onSelectAllRows={(checked) =>
            onSelectAllRows(
              checked,
              tableData?.map((row) => row.id)
            )
          }
          action={
            <Tooltip title="Delete">
              <IconButton color="primary" onClick={handleOpenConfirm}>
                <Iconify icon="eva:trash-2-outline" />
              </IconButton>
            </Tooltip>
          }
        />
        <Scrollbar>
          {/* {isLoadingmarketCategories && Spinner} */}
          {/* {!isLoadingmarketCategories && ( */}
            <Table
              size={dense ? "small" : "medium"}
              sx={{ ...tableHolderSx }}
              ref={SubCategoriesRef}
            >
              <TableHeadCustom
                order={order}
                orderBy={orderBy}
                headLabel={TABLE_HEAD}
                rowCount={tableData?.length}
                numSelected={selected.length}
                onSort={onSort}
                onSelectAllRows={(checked) =>
                  onSelectAllRows(
                    checked,
                    tableData?.map((row) => row.id)
                  )
                }
              />
              <TableBody>
                {dataFiltered
                  ?.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row, index) => (
                    <MarketSubCategoryTableRow
                      key={row.id}
                      row={row}
                      selected={selected.includes(row.id)}
                      onSelectRow={() => onSelectRow(row.id)}
                      onDeleteRow={() => handleDeleteRow(row.id)}
                    />
                  ))}

                <TableEmptyRows
                  height={denseHeight}
                  emptyRows={emptyRows(page, rowsPerPage, tableData?.length)}
                />

                <TableNoData isNotFound={isNotFound} />
              </TableBody>
            </Table>
          {/* )} */}
        </Scrollbar>

        {/* --- Strat Table Pagenation --- */}
        <MarketSubCategoryPagination
          count={dataFiltered?.length}
          page={page}
          rowsPerPage={rowsPerPage}
          onPageChange={onChangePage}
          onRowsPerPageChange={onChangeRowsPerPage}
          dense={dense}
          onChangeDense={onChangeDense}
        />
      </StyledTableContainer>

      {/* ---------- Start Confirm Dialog  -------*/}
      <CustomConfirmDialog
        open={openConfirm}
        onClose={handleCloseConfirm}
        title={translate("delete_item")}
        content={<>{translate("are_you_sure_you_want_to_delete_this_item")}</>}
        action={
          <>
            <Button
              sx={cancelBtnSx}
              onClick={() => {
                handleCloseConfirm();
              }}
            >
              {translate("cancel")}
            </Button>
            <Button
              variant="contained"
              color="primary"
              sx={confirmBtnSx}
              onClick={() => {
                handleDeleteRows(selected);
                handleCloseConfirm();
              }}
            >
              {translate("delete")}
            </Button>
          </>
        }
      />
    </>
  );
}

