import React, { useState, useEffect } from "react";
// helmet
import { Helmet } from "react-helmet-async";
// localization
import { useLocales } from "../../../locales";
// react router dom
import { Link as RouterLink, useNavigate } from "react-router-dom";
// form
import * as Yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
// @mui
import {
  Alert,
  Box,
  Button,
  Container,
  Grid,
  Stack,
  Typography,
  IconButton,
  InputAdornment,
  useTheme,
  MenuItem,
  OutlinedInput,
} from "@mui/material";
import Link from "@mui/material/Link";
// import Modal from '@mui/material/Modal';
import { LoadingButton } from "@mui/lab";
// components
import FormProvider, {
  RHFSelect,
  RHFTextField,
  RHFRadioGroup,
} from "../../../components/hook-form";
import { useSettingsContext } from "../../../components/settings";
import BreadCrumbs from "../../../components/breadCrumbs/BreadCrumbs";
import Iconify from "../../../components/iconify/Iconify";
import { addFacilityAdmin } from "../../../redux/services/get_resturant_admins_service";
import { useDispatch, useSelector } from "../../../redux/store";
import {
  getAllBranches,
  getAllBranchesMarket,
} from "../../../redux/services/restaurant_service";
import CustomDataPicker from "../../../components/custom-datepicker/CustomDatePicker";
import moment from "moment";
// --------------------------------------------------------------------------------------

//* ------- Styles --------
const containerSx = {
  px: {
    xs: "15px",
    lg: "0px!important",
  },
};

const branchFormHolderSx = {
  p: {
    xs: "33px 20px 33px 20px",
    sm: "33px 20px 33px 20px",
    md: "49px 40px 33px 40px",
  },
  borderRadius: "10px",
  background: "#FFFFFF",
  boxShadow: "2px 2px 10px rgba(0, 0, 0, 0.1)",
};

const txtFieldBorderStyle = {
  "& .MuiOutlinedInput-input": {
    fontSize: "16px",
    fontWeight: 400,
    lineHeight: "16px",
    color: "#121212",
  },
  "& .MuiInputLabel-root": {
    color: "#9DA0A4",
    fontWeight: 400,
    fontSize: "14px",
  },
  "& .MuiOutlinedInput-root": {
    // pr: "20px",
    height: "48px",
    // alignItems: 'baseline',
    "& fieldset": {
      border: "1.5px solid #bcbcbc",
    },
    "&:hover fieldset": {
      borderColor: "rgba(255, 204, 99, 0.3)",
    },
    "&.Mui-focused fieldset": {
      borderColor: "rgba(255, 204, 99, 0.3)",
    },
  },
};

const titleSx = {
  lineHeight: "30px",
  color: "#121212",
};

const transparentBorderTxtField = {
  alignItem: "center",
  "& .MuiOutlinedInput-input": {
    fontSize: "14px",
    fontWeight: 400,
    lineHeight: "16px",
    color: "#535353",
    p: "0px!important",
  },
  "& .MuiInputLabel-root": {
    color: "#121212",
    fontWeight: 500,
  },
  "& .MuiOutlinedInput-root": {
    pr: "0px",
    alignItems: "baseline",
    "& fieldset": {
      borderColor: "transparent!important",
    },
    "&:hover fieldset": {
      borderColor: "transparent!important",
    },
    "&.Mui-focused fieldset": {
      borderColor: "transparent!important",
    },
  },
};

const loadingButtonStyle = {
  bgcolor: "#FFCC63",
  height: "48px",
  color: (theme) =>
    theme.palette.mode === "light" ? "common.white" : "grey.800",
  "&:hover": {
    bgcolor: "text.primary",
    color: (theme) =>
      theme.palette.mode === "light" ? "common.white" : "grey.800",
  },
  fontSize: "16px",
  fontWeight: 600,
};

const cancelBtnSx = {
  ...loadingButtonStyle,
  bgcolor: "#fff",
  height: "48px",
  border: "1px solid rgba(255, 204, 99)",
  color: (theme) =>
    theme.palette.mode === "light" ? "primary.light" : "grey.800",
  ":hover": {
    bgcolor: "#ffcc63",
    color: "#fff",
  },
};

const positions = [
  {
    name: "regional_manager",
    Role: 1,
  },
  {
    name: "branch_manager",
    Role: 2,
  },
  {
    name: "chasher",
    Role: 3,
  },
];

const genderTextSx = {
  textAlign: "right",
  color: "#121212",
  fontWeight: "500",
  fontSize: "16px",
  // paddingTop: "32px"
};

const genderHolderSx = {
  display: "flex",
  flexDirection: "row",
  alignItems: "center",
  justifyContent: "space-between",
  // mb: "23px",
  "& .MuiFormHelperText-root.Mui-error": {
    position: "absolute",
    top: "30px",
  },
};

const genderRadioSx = {
  "& .MuiFormControlLabel-root": {
    mr: "32px",
  },
  "& .MuiSvgIcon-root": {
    color: "#ffcc63",
  },

  "& .MuiFormControlLabel-label": {
    fontSize: "12px",
    fontWeight: 400,
    color: "#bcbcbc",
  },
};

const genderOptions = [
  { value: "0", label: "male" },
  { value: "1", label: "female" },
];

export default function AddNewBranchPage() {
  const dispatch = useDispatch();

  useEffect(() => {
    if (localStorage.getItem("facilityType") === "0") {
      dispatch(getAllBranches(1));
    }
    if (localStorage.getItem("facilityType") === "1") {
      dispatch(getAllBranchesMarket(1));
    }
  }, [dispatch]);

  const [allBranches, setAllBranches] = useState([]);

  const { branches } = useSelector((state) => state.branches);

  const { isAdded, isLoadingFacilityAdmin } = useSelector(
    (state) => state.employees
  );

  const { branchesMarket } = useSelector((state) => state.market);

  useEffect(() => {
    setAllBranches(
      localStorage.getItem("facilityType") === "0" ? branches : branchesMarket
    );
  }, [branches, branchesMarket]);

  const [ProfilePicture, setSelectedFile] = React.useState(null);

  const [showPassword, setShowPassword] = useState(false);

  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const { themeStretch } = useSettingsContext();

  const { translate } = useLocales();

  const navigate = useNavigate();

  const [selectedNames, setSelectedNames] = useState([]);

  const [valueType, setValueType] = useState(false);
  console.log(valueType);

  const AddEmployeeSchema = Yup.object().shape({
    Gender: Yup.string().required(translate("gender_is_required")),
    //  ProfilePicture: Yup.string().required(
    //     translate("profile_picture_name_is_required")
    //   ),
    DateOfBirht: Yup.string()
      .required(translate("date_of_birth_is_required"))
      .nullable(),
    FirstName: Yup.string().required(translate("first_name_is_required")),
    LastName: Yup.string().required(translate("second_name_is_required")),
    Email: Yup.string()
      .required(translate("email_is_required"))
      .email("email_must_be_a_valid_email_address"),
    Address: Yup.string().required(translate("address_is_required")),
    ProfilePicture: Yup.string().required(
      translate("profile_picture_is_required")
    ),
    PhoneNumber: Yup.string().required(translate("phone_is_required")),
    Role: Yup.string().required(translate("position_is_required")),
    FacilityIds: valueType
      ? Yup.array().required(translate("branch_is_required")).nullable()
      : Yup.string().required(translate("branch_is_required")),
    countryCode: Yup.string().required(translate("country_code_is_required")),
    Password: Yup.string().required(translate("password_is_required")),
    ConfirmPassword: Yup.string()
      .required(translate("confirm_password_is_required"))
      .oneOf([Yup.ref("Password")], translate("passwords_do_not_match")),
  });

  const defaultValues = {
    FirstName: "",
    LastName: "",
    Email: "",
    Address: "",
    PhoneNumber: "",
    Role: "",
    FacilityIds: valueType ? [] : "",
    countryCode: "+02",
    Password: "",
    ConfirmPassword: "",
    Gender: "",
    ProfilePicture: "",
    DateOfBirht: new Date(),
  };

  const methods = useForm({
    resolver: yupResolver(AddEmployeeSchema),
    defaultValues,
  });

  const {
    reset,
    setError,
    setValue,
    watch,
    handleSubmit,
    formState: { errors, isSubmitting, isSubmitSuccessful },
  } = methods;

  const watchRole = watch("Role");

  const onSubmit = async (data) => {
    let { FacilityIds, DateOfBirht } = data;
    DateOfBirht = convertDateFormat(DateOfBirht);
    data = {
      ...data,
      FacilityIds: valueType ? FacilityIds : [FacilityIds],
    };

    let formData = new FormData();
    formData.append("ProfilePicture", ProfilePicture);

    formData.append("DateOfBirht", data.DateOfBirht);

    formData.append("Password", data.Password);

    formData.append("Email", data.Email);

    formData.append("Gender", data.Gender);

    formData.append("PhoneNumber", data.PhoneNumber);

    formData.append("LastName", data.LastName);

    formData.append("FirstName", data.FirstName);

    formData.append("Address", data.Address);

    formData.append("Role", data.Role);

    for (let i = 0; i < data.FacilityIds.length; i++) {
      formData.append("FacilityIds", data.FacilityIds[i]);
    }

    try {
      // code here

      dispatch(addFacilityAdmin(formData));
    } catch (error) {
      console.error(error);
      reset();
      setError("afterSubmit", {
        ...error,
        message: error.message,
      });
    }
  };

  const breadcrumbs = [
    <Link
      sx={{
        fontWeight: 400,
        fontSize: "12px",
        color: "#9DA0A4",
      }}
      underline="hover"
      key="1"
      component={RouterLink}
      to="/dashboard/users"
    >
      {translate(`employees`)}
    </Link>,

    <Typography
      sx={{
        fontWeight: "700 !important",
        fontSize: "12px !important",
        color: "#121212",
      }}
      key="2"
    >
      {translate(`add_new_employee`)}
    </Typography>,
  ];

  // --------------- JSX Code -----------------
  return (
    <>
      <Helmet>
        <title> Add Employee Information </title>
      </Helmet>

      <Container maxWidth={themeStretch ? false : "xl"}>
        <BreadCrumbs breadcrumbs={breadcrumbs} />

        <Box sx={{ ...branchFormHolderSx }}>
          {/* ******* Title ******** */}
          <Stack direction="row" mb="30px" alignItems="center">
            <Typography variant="h5" sx={{ ...titleSx }}>
              {translate(`add_new_employee`)}
            </Typography>
          </Stack>

          {/* ***************** Form **************** */}
          <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
            {!!errors.afterSubmit && (
              <Alert severity="error">{errors.afterSubmit.message}</Alert>
            )}

            {/* *********** Add New Employee ********** */}

            <Grid container columnSpacing={{ xs: "40px", lg: "90px" }}>
              {/* ******** Left Side Inputs ******* */}
              <Grid item xs={12} sm={6} md={6}>
                {/*====== employee name ====== */}
                <Grid item xs={12} mb={{ xs: "16px", md: "0px" }}>
                  <Grid container columnSpacing={2}>
                    {/*--- employee first name  --- */}
                    <Grid item xs={12} sm={12} md={6} mb="16px">
                      <RHFTextField
                        name="FirstName"
                        label={translate("first_name")}
                        sx={{ ...txtFieldBorderStyle }}
                      />
                    </Grid>

                    {/*--- employee second name --- */}
                    <Grid item xs={12} sm={12} md={6}>
                      <RHFTextField
                        name="LastName"
                        label={translate("second_name")}
                        sx={{ ...txtFieldBorderStyle }}
                      />
                    </Grid>
                  </Grid>
                </Grid>

                {/*====== employee email ======= */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="Email"
                    type="email"
                    label={translate("email")}
                    sx={{ ...txtFieldBorderStyle }}
                  />
                </Grid>

                {/*====== employee Phone ====== */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="PhoneNumber"
                    type="number"
                    label={translate("employee_phone")}
                    placeholder={translate("add_phone")}
                    sx={{
                      ...txtFieldBorderStyle,
                      "& .MuiOutlinedInput-input": {
                        fontSize: "16px",
                        fontWeight: 400,
                        lineHeight: "16px",
                        color: "#121212",
                        pl: "10px",
                      },
                      "& .MuiOutlinedInput-root": {
                        pr: "20px",
                        alignItems: "baseline",
                        "& fieldset": {
                          borderColor: "#D8D8D8",
                        },
                        "&:hover fieldset": {
                          borderColor: "rgba(255, 204, 99, 0.3)",
                        },
                        "&.Mui-focused fieldset": {
                          borderColor: "rgba(255, 204, 99, 0.3)",
                        },
                      },
                    }}
                    InputProps={{
                      startAdornment: (
                        <RHFSelect
                          name="countryCode"
                          sx={{
                            ...transparentBorderTxtField,
                            "& .MuiSvgIcon-root": {
                              right: "-6px!important",
                              top: "0%",
                            },
                            width: "80px",
                          }}
                          SelectProps={{
                            native: false,
                            // IconComponent: () => (
                            //   <img
                            //     src="/assets/icons/dashboard/ic_dropdown.svg"
                            //     alt=""
                            //   />
                            // ),
                          }}
                          onChange={(e) =>
                            setValue("countryCode", e.target.value, {
                              shouldValidate: true,
                            })
                          }
                        >
                          <MenuItem value="+02">+02 EG</MenuItem>
                          <MenuItem value="+03">+03 US</MenuItem>
                        </RHFSelect>
                      ),
                    }}
                  />
                </Grid>

                {/*======= employee Position  ======= */}
                <Grid item xs={12} mb="16px">
                  <RHFSelect
                    name="Role"
                    type="text"
                    label={translate("employee_position")}
                    placeholder={translate("select_employee_position")}
                    sx={{ ...txtFieldBorderStyle }}
                    onChange={(e) => {
                      setValue("Role", e.target.value, {
                        shouldValidate: true,
                      });
                      e.target.value === 1
                        ? setValueType(true)
                        : setValueType(false);
                    }}
                    SelectProps={{
                      native: false,
                      // IconComponent: () => (
                      //   <img
                      //     src="/assets/icons/dashboard/ic_dropdown.svg"
                      //     alt=""
                      //   />
                      // ),
                    }}
                  >
                    {positions.map((row, index) => (
                      <MenuItem value={row?.Role} key={index}>
                        {translate(`${row?.name}`)}
                      </MenuItem>
                    ))}
                  </RHFSelect>
                </Grid>

                {/*======= employee Branch case(Regional Manager)  ======= */}
                {watchRole === 1 && (
                  <Grid item xs={12} mb="16px">
                    <RHFSelect
                      multiple
                      name="FacilityIds"
                      type="text"
                      label={translate("employee_branch")}
                      placeholder={translate("select_branch")}
                      sx={{ ...txtFieldBorderStyle }}
                      onChange={(e) => {
                        setValue("FacilityIds", e.target.value, {
                          shouldValidate: true,
                        });
                        setSelectedNames(e.target.value);
                      }}
                      SelectProps={{
                        multiple: true,
                        native: false,
                        value: selectedNames,
                        // IconComponent: () => (
                        //   <img
                        //     src="/assets/icons/dashboard/ic_dropdown.svg"
                        //     alt=""
                        //   />
                        // ),
                        // InputProbs: <OutlinedInput label="Multiple Select" />
                      }}
                    >
                      {allBranches?.map((row, index) => (
                        <MenuItem value={row?.id} key={index}>
                          {translate(`${row?.name}`)}
                        </MenuItem>
                      ))}
                    </RHFSelect>
                  </Grid>
                )}

                {/*======= employee Branch case(any Position)  ======= */}
                {watchRole !== 1 && (
                  <Grid item xs={12} mb="16px">
                    <RHFSelect
                      name="FacilityIds"
                      type="text"
                      label={translate("employee_branch")}
                      placeholder={translate("select_branch")}
                      sx={{ ...txtFieldBorderStyle }}
                      onChange={(e) => {
                        setValue(`FacilityIds`, e.target.value, {
                          shouldValidate: true,
                        });
                      }}
                      SelectProps={{
                        multiple: false,
                        native: false,
                        // IconComponent: () => (
                        //   <img
                        //     src="/assets/icons/dashboard/ic_dropdown.svg"
                        //     alt=""
                        //   />
                        // ),
                      }}
                    >
                      {allBranches?.map((row, index) => (
                        <MenuItem value={row?.id} key={index}>
                          {translate(`${row?.name}`)}
                        </MenuItem>
                      ))}
                    </RHFSelect>
                  </Grid>
                )}
              </Grid>

              {/* ************* Right Side Inputs ************ */}
              <Grid item xs={12} sm={6} md={6}>
                {/*====== emplyee Address  ====== */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="Address"
                    label={translate("employee_address")}
                    type="text"
                    sx={{ ...txtFieldBorderStyle }}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <img
                            src="/assets/icons/auth/ic_location.svg"
                            alt=""
                          />
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>

                {/*====== employee Password ====== */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="Password"
                    label={translate("password")}
                    type={showPassword ? "text" : "password"}
                    sx={txtFieldBorderStyle}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton
                            onClick={() => setShowPassword(!showPassword)}
                            edge="end"
                          >
                            <Iconify
                              icon={
                                showPassword
                                  ? "eva:eye-outline"
                                  : "eva:eye-off-outline"
                              }
                              color={showPassword ? "#FFCC63" : "#BCBCBC"}
                            />
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>

                {/*====== employee Confirm Password ====== */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="ConfirmPassword"
                    label={translate("confirm_password")}
                    type={showConfirmPassword ? "text" : "password"}
                    sx={txtFieldBorderStyle}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton
                            onClick={() =>
                              setShowConfirmPassword(!showConfirmPassword)
                            }
                            edge="end"
                          >
                            <Iconify
                              icon={
                                showConfirmPassword
                                  ? "eva:eye-outline"
                                  : "eva:eye-off-outline"
                              }
                              color={
                                showConfirmPassword ? "#FFCC63" : "#BCBCBC"
                              }
                            />
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>

                {/*======= employee Date of birth   ======= */}
                <Grid item xs={12} mb="16px">
                  <CustomDataPicker
                    name="DateOfBirht"
                    textLabel="date_of_birth"
                    sx={{ ...txtFieldBorderStyle, width: "100%" }}
                  />
                </Grid>

                {/*======= employee picture   ======= */}
                <Grid item xs={12} mb="16px">
                  <RHFTextField
                    name="ProfilePicture"
                    label={translate("profile_picture")}
                    type="file"
                    onChange={(event) => {
                      console.log(event.target.files[0]);
                      setSelectedFile(event.target.files[0]);
                      setValue("ProfilePicture", event.target.value, {
                        shouldValidate: true,
                      });
                    }}
                    // label={translate("user image")}
                    sx={{
                      ...txtFieldBorderStyle,
                      "& .MuiOutlinedInput-input": {
                        color: ProfilePicture ? "#535353" : "transparent",
                        fontSize: "14px",
                      },
                    }}
                    inputProps={{
                      sx: {
                        "&::placeholder": {
                          textAlign: "left",
                          color: "#BCBCBC",
                          fontWeight: "400",
                          fontSize: "14px",
                          lineHeight: "22px",
                        },
                      },
                    }}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton
                            color="primary"
                            aria-label="upload picture"
                            component="label"
                          >
                            <label htmlFor="licenses">
                              <img
                                src="/assets/icons/auth/ic_upload.svg"
                                alt="upload_icon"
                              />
                            </label>
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>

                {/*======= employee gender   ======= */}
                <Grid item xs={12} mb="16px">
                  <Stack sx={{ ...genderHolderSx, position: "relative" }}>
                    <Typography sx={{ ...genderTextSx }}>
                      {translate("gender")}
                    </Typography>
                    <RHFRadioGroup
                      name="Gender"
                      // defaultValues={defaultValues.Gender}
                      options={genderOptions}
                      sx={{ ...genderRadioSx }}
                      //  placeholder={Gender}
                    />
                  </Stack>
                </Grid>
              </Grid>
            </Grid>

            {/* ==============@@ Actions Button Button  @@=============*/}
            <Grid container columnSpacing={{ xs: "40px", lg: "90px" }}>
              <Grid item xs={12} sm={6} md={6}>
                {""}
              </Grid>

              <Grid item xs={12} sm={6} md={6} mt="34px">
                <Stack direction="row">
                  <Button
                    color="inherit"
                    size="large"
                    variant="Contained"
                    // loading={isSubmitSuccessful || isSubmitting}
                    sx={{ ...cancelBtnSx, width: "100%", mr: "16px" }}
                    onClick={() => {
                      // handleOpenConfirm();
                      navigate("/dashboard/users");
                    }}
                  >
                    {translate("back")}
                  </Button>
                  <LoadingButton
                    color="inherit"
                    size="large"
                    type="submit"
                    variant="Contained"
                    loading={isLoadingFacilityAdmin}
                    sx={{ ...loadingButtonStyle, width: "100%" }}
                  >
                    {translate("add_employee")}
                  </LoadingButton>
                </Stack>
              </Grid>
            </Grid>
          </FormProvider>
        </Box>
      </Container>
    </>
  );
}

function convertDateFormat(value) {
  const result = moment(value).format().split("T")[0];
  return result;
}
