import * as React from "react";
import Box from "@mui/material/Box";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";

// --------------------------------------
const steps = [
  {
    index: 1,
    activeIcon: <img src="/assets/icons/auth/ic_activeRestaurant.svg" alt="" />,
    inActiveIcon: (
      <img src="/assets/icons/auth/ic_inActiveRestaurant.svg" alt="" />
    ),
    completedIcon: (
      <img src="/assets/icons/auth/ic_CompleteRestaurant.svg" alt="" />
    ),
  },
  {
    index: 2,
    activeIcon: <img src="/assets/icons/auth/ic_activePersonal.svg" alt="" />,
    inActiveIcon: <img src="/assets/icons/auth/ic_activePersonal.svg" alt="" />,
    completedIcon: (
      <img src="/assets/icons/auth/ic_completePersonal.svg" alt="" style={{}} />
    ),
  },
  {
    index: 3,
    activeIcon: <img src="/assets/icons/auth/ic_activeBank.svg" alt="" />,
    inActiveIcon: <img src="/assets/icons/auth/ic_inActiveBank.svg" alt="" />,
    completedIcon: null,
  },
];

// ------ Styles -----
const stepperStyle = {
  width: { xs: "60%", sm: "50%" },
  m: "auto",
  "& .MuiStepConnector-root": {
    top: "20px",
    "& .MuiStepConnector-line": {
      borderWidth: "1px",
      borderStyle: "dashed",
      borderTopWidth: "0px",
    },
  },
  "& .MuiStepIcon-root": {
    width: "45px",
    height: "45px",
    border: "1px solid rgba(255, 204, 99, 0.6)",
    borderRadius: "50%",
    color: "#fff",
  },
  "& .MuiStepIcon-text": {
    fill: "#D8D8D8",
  },
  "& .MuiStepConnector-root.Mui-active": {
    "& .MuiStepConnector-line": {
      borderColor: "#FFCC63",
    },
  },
  "& .MuiStepConnector-root.Mui-completed": {
    "& .MuiStepConnector-line": {
      borderColor: "#FFCC63",
    },
  },
};

const stepSyle = {
  width: "45px",
  height: "45px",
  "& .MuiStepLabel-label.Mui-active": {
    color: "#FFCC63",
  },
  "& .MuiStepLabel-label.Mui-completed": {
    color: "#FFCC63",
    fontWeight: 800,
  },
};

const activeIconHolderSx = {
  border: "2px solid #FFCC63",
  background: "#FFFFFF",
  p: "10px",
  borderRadius: "50%",
  boxShadow: "0px 2px 4px rgba(61, 61, 61, 0.1)",
};

const inActiveIconHolderSx = {
  background: "#FFFFFF",
  p: "10px",
  borderRadius: "50%",
  boxShadow: "0px 2px 4px rgba(61, 61, 61, 0.1)",
};

const completeIconHolderSx = {
  background: "#FFCC63",
  p: "10px",
  borderRadius: "50%",
  boxShadow: "0px 2px 4px rgba(61, 61, 61, 0.1)",
};

export default function CustomStepper({ activeStep }) {
  // ------ JSX Code -----
  return (
    <Box sx={{ width: "100%" }}>
      <Stepper
        sx={{ ...stepperStyle }}
        activeStep={activeStep}
        alternativeLabel
      >
        {steps.map((step, index) => (
          <Step sx={{ ...stepSyle }} key={index}>
            <StepLabel
              icon={
                <Box
                  // sx={{ ...activeIconHolderSx }}
                  sx={
                    activeStep === step.index
                      ? { ...activeIconHolderSx }
                      : activeStep > step.index
                      ? { ...completeIconHolderSx }
                      : { ...inActiveIconHolderSx }
                  }
                >
                  {activeStep === step.index
                    ? step.activeIcon
                    : activeStep > step.index
                    ? step.completedIcon
                    : step.inActiveIcon}
                </Box>
              }
              onClick={() => console.log("Clicked")}
            />
          </Step>
        ))}
      </Stepper>
    </Box>
  );
}
